UPDATE saas_resources
res_name = #{resName}, parent_id = #{parentId}, res_url = #{resUrl}, create_date = #{createDate},
WHERE id = #{id}

mapper.delete:
DELETE FROM saas_resources


        AND id = #{id}


        AND res_name = #{resName}


        AND parent_id = #{parentId}


        AND res_url = #{resUrl}


        AND create_date = #{createDate}

