mapper.select:
select
DISTINCT uu.id as universityId,uu.name as universityName
zz.majorCode as majorCode,zz.major as majorName,uu.id as universityId,uu.name as universityName,uu.rank as rank,dd.name as batchName,tmp.planEnrolling as planNumber,uu.property as property, zz.selectSubject as selSubject
from zgk2.zgk_university_major_enrolling_plan_zj tmp INNER join zgk2.zgk_zj_3in7 zz on tmp.majorCode=zz.majorCode and tmp.universityId=zz.universityId INNER join zgk2.zgk_university uu on uu.id=tmp.universityId left join zgk2.zgk_data_dict dd on dd.type='BATCHTYPE' and dd.dictId=tmp.batch where 1=1 and tmp.areaId=#{areaId} and tmp.year=#{year}
and zz.selectCount!=0 and zz.selectSubject like CONCAT('%',#{subject},'%')
and zz.selectCount=0
and uu.name like concat('%',#{universityName},'%')
and uu.id = #{universityId}
group by tmp.majorType,zz.eduLevel,tmp.universityId,zz.major order by uu.educationLevel,if(isnull(uu.rank),10000,uu.rank) asc limit ${offset}, ${rows}

select count(*) from (select count(zz.majorCode) from zgk2.zgk_university_major_enrolling_plan_zj tmp INNER join zgk2.zgk_zj_3in7 zz on tmp.majorCode=zz.majorCode and tmp.universityId=zz.universityId INNER join zgk2.zgk_university uu on uu.id=tmp.universityId left join zgk2.zgk_data_dict dd on dd.type='BATCHTYPE' and dd.dictId=tmp.batch where 1=1 and tmp.areaId=#{areaId} and tmp.year=#{year}
and zz.selectCount!=0 and zz.selectSubject like CONCAT('%',#{subject},'%')
and zz.selectCount=0
and uu.name like concat('%',#{universityName},'%')
and uu.id = #{universityId}
group by tmp.majorType,zz.eduLevel,tmp.universityId,zz.major) tmp2

select count(DISTINCT tmp.batch) from zgk2.zgk_university_major_enrolling_plan_zj tmp INNER join zgk2.zgk_zj_3in7 zz on tmp.majorCode=zz.majorCode and tmp.universityId=zz.universityId INNER join zgk2.zgk_university uu on uu.id=tmp.universityId where 1=1 and tmp.areaId=#{areaId} and tmp.year=#{year}
and zz.selectCount!=0 and zz.selectSubject like CONCAT('%',#{subject},'%')
and zz.selectCount=0

select count(DISTINCT bb.discipline_code) from zgk2.zgk_university_major_enrolling_plan_zj tmp INNER join zgk2.zgk_zj_3in7 zz on tmp.majorCode=zz.majorCode and tmp.universityId=zz.universityId INNER join zgk2.zgk_university uu on uu.id=tmp.universityId INNER join zgk2.zgk_major_base bb on bb.major_type=1 and bb.major_code=tmp.majorCode where 1=1 and tmp.areaId=#{areaId} and tmp.year=#{year}
and zz.selectCount!=0 and zz.selectSubject like CONCAT('%',#{subject},'%')
and zz.selectCount=0

select dd.selectCourses from saas_exam_detail dd where dd.examId=#{examId}
ORDER BY totleScore DESC

  limit ${top};
